function com_install_outputErrors( $errors ) {
    if( $errors ) {
        echo "<p>Smartformer was unable to copy files into:<br/>";
        foreach( $errors as $key => $error ) {
            list($type,$path) = $error;
            if( 'copy' == $type ) {
                echo $path . "<br/>";
                unset($errors[$key]);
            }
        }
        echo "</p>";
        if($errors) {
            echo "<p>Smartformer was unable to create files into:<br/>";
            foreach( $errors as $key => $error ) {
                list($type,$path) = $error;
                if( 'create' == $type ) {
                    echo $path . "<br/>";
                }
            }
            echo "</p>";
        }
        echo "<p style='color: red;'><b>Some features may be unavailable. Please open write access into mentioned folders and reinstall the component.</b></p>";
    }
}

